import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../actions/login';
import './LoginForm.css';

class LoginForm extends Component {

  constructor(props) {
    super(props);
    this.state = {
      username : "",
      password : "",
      isFormSubmit : false,
    };
    this.onSubmit = this.onSubmit.bind(this);
  }

  render() {
    let {username, password} = this.state;
    let {loginStatus} = this.props;
    console.log("render")
    return (
      <form name="loginForm" onSubmit={this.onSubmit}>
        <div className="form-group-collection">
          <div className="form-group">
            <label>Username</label>
            <input type="username" name="username" onChange={e => this.setState({username: e.target.value})} value={username}/>
          </div>

          <div className="form-group">
            <label>Password</label>
            <input type="password" name="password" onChange={e => this.setState({password: e.target.value})} value={password}/>
          </div>
        </div>

        <input type="submit" value="Login" />
        { loginStatus && <div>Success.</div> }
        {!loginStatus && this.state.isFormSubmit&& <div>invalid Username and password </div>}
        {/* { loginError && <div>{loginError.message}</div> } */} 
      </form>
    )
  }

  onSubmit(e) {
    e.preventDefault();
    let { username, password } = this.state;
    console.log("kdjfkdjkfjdk",username,password)
    this.props.login(username, password);
    this.setState({
      isFormSubmit : true,

      username: '',
      password: ''
    });
  }
}

const mapStateToProps = (state) => {
  return {
    // isLoginPending: state.isLoginPending,
    // isLoginSuccess: state.isLoginSuccess,
    // loginError: state.loginError
    loginStatus: state.login.loginStatus
  };
}

const mapDispatchToProps = (dispatch) => {
  return {
    login: (username, password) => dispatch(login(username, password))
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(LoginForm);
